function genSku() {const s = (document.getElementById('shape')?.value || "").toUpperCase().replace(/\s+/g,'');const l = (document.getElementById('length')?.value || "").toUpperCase();const f = (document.getElementById('finish')?.value || "").toUpperCase().replace(/\s+/g,'-');const d = (document.getElementById('design')?.value || "").toUpperCase().replace(/\s+/g,'-');const k = (document.getElementById('sizekit')?.value || "").toUpperCase();const sku = `NAIL-${s}-${l}-${f}-${d}-${k}`;const skuOut = document.getElementById('skuOut');if (skuOut) {skuOut.textContent = sku;skuOut.classList.add('fade-in')} } function updatePricing() {const cogs = document.getElementById('cogsRange')?.value || 8;const msrp = document.getElementById('msrpRange')?.value || 39;const cogsValue = document.getElementById('cogsValue');const msrpValue = document.getElementById('msrpValue');const marginValue = document.getElementById('marginValue');const marginBar = document.getElementById('marginBar');const roasValue = document.getElementById('roasValue');const roasBar = document.getElementById('roasBar');if (cogsValue) cogsValue.textContent = '$' + cogs;if (msrpValue) msrpValue.textContent = '$' + msrp;const margin = ((msrp - cogs) / msrp * 100).toFixed(0);if (marginValue) marginValue.textContent = margin + '%';if (marginBar) {marginBar.style.width = margin + '%';marginBar.classList.add('progress-animation')} const fees = 6;const roas = (msrp / (msrp - cogs - fees)).toFixed(2);if (roasValue) roasValue.textContent = roas + 'x';if (roasBar) {roasBar.style.width = Math.min((roas / 5) * 100, 100) + '%';roasBar.classList.add('progress-animation')} } function calcROAS() {const c = parseFloat(document.getElementById('cogs')?.value || 0);const m = parseFloat(document.getElementById('msrp')?.value || 0);const f = parseFloat(document.getElementById('fees')?.value || 0);const denom = (m - c - f);const roasOut = document.getElementById('roasOut');if (!roasOut) return;if (denom <= 0 || m <= 0) {roasOut.textContent = "Check inputs ‚Äî margin is non‚Äëpositive.";roasOut.style.color = '#f44336'} else {const be = (m / denom);roasOut.textContent = "Breakeven ROAS: " + be.toFixed(2) + "x";roasOut.style.color = '#4caf50'} } function autoSave() {const inputs = document.querySelectorAll('input, select, textarea');inputs.forEach(input => {if (input.id) {localStorage.setItem(`moth_${input.id}`, input.value)} })} function loadSavedData() {const inputs = document.querySelectorAll('input, select, textarea');inputs.forEach(input => {if (input.id) {const saved = localStorage.getItem(`moth_${input.id}`);if (saved !== null) {input.value = saved} } })} document.addEventListener('DOMContentLoaded', function() {loadSavedData();const inputs = document.querySelectorAll('input, select, textarea');inputs.forEach(input => {input.addEventListener('input', autoSave);input.addEventListener('change', autoSave)});if (document.getElementById('cogsRange')) {updatePricing()} });
class DataManager {constructor() {this.storageKey = 'mothEmporiumBusinessData';this.data = this.loadData();this.init()} init() {this.bindEvents();this.autoSave()} loadData() {try {const stored = localStorage.getItem(this.storageKey);return stored ? JSON.parse(stored) : this.getDefaultData()} catch (error) {console.warn('Failed to load data from localStorage:', error);return this.getDefaultData()} } getDefaultData() {return {businessInfo: {brandName: 'Moth Emporium', tagline: 'Nail art as your canvas - Where Beauty Takes Flight', launchDate: '', targetOrders: 100, budget: 5000 }, products: {designs: [], pricing: {baseCOGS: 8, targetMargin: 65, msrp: 39 } }, content: {heroDesigns: 10, categories: {evergreen: false, trendy: false, premium: false }, requirements: {lifestylePhotos: false, applicationVideo: false, macroShots: false, qrIntegration: false } }, progress: {designComplete: 0, contentReady: 0, launchReady: 0, completedTasks: [] }, analytics: {roas: 2.5, aov: 0, cac: 0, conversionRate: 0 }, lastUpdated: new Date().toISOString() }} saveData() {try {this.data.lastUpdated = new Date().toISOString();localStorage.setItem(this.storageKey, JSON.stringify(this.data));this.showSaveIndicator()} catch (error) {console.error('Failed to save data:', error);this.showErrorIndicator()} } autoSave() {setInterval(() => {this.saveData()}, 30000)} bindEvents() {document.addEventListener('input', (e) => {if (e.target.matches('input, select, textarea')) {this.updateField(e.target)} });document.addEventListener('change', (e) => {if (e.target.matches('input[type="checkbox"]')) {this.updateField(e.target)} });window.addEventListener('beforeunload', () => {this.saveData()})} updateField(element) {const fieldName = element.id || element.name;if (!fieldName) return;let value = element.value;if (element.type === 'checkbox') {value = element.checked} else if (element.type === 'number') {value = parseFloat(value) || 0} this.setNestedValue(fieldName, value);this.saveData()} setNestedValue(path, value) {const keys = path.split('.');let current = this.data;for (let i = 0;i < keys.length - 1;i++) {if (!current[keys[i]]) {current[keys[i]] = {}} current = current[keys[i]]} current[keys[keys.length - 1]] = value} getNestedValue(path) {const keys = path.split('.');let current = this.data;for (const key of keys) {if (current && typeof current === 'object' && key in current) {current = current[key]} else {return undefined} } return current} updateProgress() {const checkboxes = document.querySelectorAll('input[type="checkbox"]');const checked = document.querySelectorAll('input[type="checkbox"]:checked');const progress = checkboxes.length > 0 ? (checked.length / checkboxes.length * 100) : 0;this.data.progress.designComplete = Math.round(progress);this.data.progress.contentReady = Math.round(progress * 0.8);this.data.progress.launchReady = Math.round(progress * 0.6);this.updateProgressDisplay()} updateProgressDisplay() {const designProgress = document.getElementById('designProgress');const contentProgress = document.getElementById('contentProgress');const launchProgress = document.getElementById('launchProgress');if (designProgress) {designProgress.textContent = this.data.progress.designComplete + '%';this.animateProgressBar(designProgress)} if (contentProgress) {contentProgress.textContent = this.data.progress.contentReady + '%';this.animateProgressBar(contentProgress)} if (launchProgress) {launchProgress.textContent = this.data.progress.launchReady + '%';this.animateProgressBar(launchProgress)} } animateProgressBar(element) {element.classList.add('pulse-effect');setTimeout(() => {element.classList.remove('pulse-effect')}, 1000)} exportData(format = 'json') {const data = {...this.data, exportDate: new Date().toISOString(), version: '1.0' };if (format === 'json') {this.downloadFile(JSON.stringify(data, null, 2), 'moth-emporium-business-plan.json', 'application/json')} else if (format === 'csv') {this.exportToCSV()} } exportToCSV() {const csvData = this.generateCSVData();this.downloadFile(csvData, 'moth-emporium-data.csv', 'text/csv')} generateCSVData() {const headers = ['Category', 'Field', 'Value', 'Last Updated'];const rows = [headers];this.flattenData(this.data, rows, '');return rows.map(row => row.map(cell => `"${cell}"`).join(',')).join('\n')} flattenData(obj, rows, prefix) {for (const [key, value] of Object.entries(obj)) {if (typeof value === 'object' && value !== null && !Array.isArray(value)) {this.flattenData(value, rows, prefix + key + '.')} else {rows.push([prefix.slice(0, -1), key, value, this.data.lastUpdated])} } } downloadFile(content, filename, mimeType) {const blob = new Blob([content], {type: mimeType });const url = URL.createObjectURL(blob);const link = document.createElement('a');link.href = url;link.download = filename;document.body.appendChild(link);link.click();document.body.removeChild(link);URL.revokeObjectURL(url)} importData(file) {const reader = new FileReader();reader.onload = (e) => {try {const importedData = JSON.parse(e.target.result);this.data = {...this.getDefaultData(), ...importedData };this.saveData();this.loadDataIntoUI();this.showSuccessIndicator('Data imported successfully!')} catch (error) {this.showErrorIndicator('Failed to import data. Please check the file format.')} };reader.readAsText(file)} loadDataIntoUI() {Object.keys(this.data).forEach(section => {this.loadSectionData(section, this.data[section])})} loadSectionData(section, data) {if (typeof data === 'object' && data !== null) {Object.keys(data).forEach(key => {const element = document.getElementById(key);if (element) {if (element.type === 'checkbox') {element.checked = data[key]} else {element.value = data[key]} } })} } showSaveIndicator() {this.showIndicator('Data saved', 'success')} showErrorIndicator(message = 'Error occurred') {this.showIndicator(message, 'error')} showSuccessIndicator(message) {this.showIndicator(message, 'success')} showIndicator(message, type) {const indicator = document.createElement('div');indicator.className = `save-indicator ${type}`;indicator.textContent = message;indicator.style.cssText = ` position: fixed;top: 20px;right: 20px;padding: 10px 20px;background: ${type === 'success' ? '#4caf50' : '#f44336'};color: white;border-radius: 4px;z-index: 10000;animation: dataSlideIn 0.3s ease-out;`;document.body.appendChild(indicator);setTimeout(() => {indicator.style.animation = 'slideOut 0.3s ease-in';setTimeout(() => {if (indicator.parentNode) {indicator.parentNode.removeChild(indicator)} }, 300)}, 2000)} resetData() {if (confirm('Are you sure you want to reset all data? This cannot be undone.')) {localStorage.removeItem(this.storageKey);this.data = this.getDefaultData();this.loadDataIntoUI();this.showSuccessIndicator('Data reset successfully')} } getBusinessSummary() {return {brandName: this.data.businessInfo.brandName, progress: this.data.progress, targetOrders: this.data.businessInfo.targetOrders, budget: this.data.businessInfo.budget, lastUpdated: this.data.lastUpdated }} } document.addEventListener('DOMContentLoaded', () => {window.dataManager = new DataManager();addExportImportButtons()});function addExportImportButtons() {const analyticsSection = document.getElementById('analytics');if (analyticsSection) {const exportDiv = document.createElement('div');exportDiv.className = 'card mystical-card floating-card';exportDiv.innerHTML = ` <h3>üíæ Data Management</h3> <div class="form-group mystical-form-group"> <button class="btn mystical-btn" onclick="window.dataManager.exportData('json')">Export JSON</button> <button class="btn mystical-btn" onclick="window.dataManager.exportData('csv')">Export CSV</button> <input type="file" id="importFile" accept=".json" style="display: none;" onchange="handleFileImport(this)"> <button class="btn mystical-btn" onclick="document.getElementById('importFile').click()">Import JSON</button> <button class="btn mystical-btn" onclick="window.dataManager.resetData()" style="background: #f44336;">Reset Data</button> </div> `;analyticsSection.appendChild(exportDiv)} } function handleFileImport(input) {const file = input.files[0];if (file) {window.dataManager.importData(file)} } const style = document.createElement('style');style.textContent = ` @keyframes dataSlideIn {from {transform: translateX(100%);opacity: 0} to {transform: translateX(0);opacity: 1} } @keyframes dataSlideOut {from {transform: translateX(0);opacity: 1} to {transform: translateX(100%);opacity: 0} } `;document.head.appendChild(style);
